import React, { Component } from "react";
import {
  Containner,
  FormWrap,
  Icon,
  FormContent,
  Form,
  FormH1,
  FormLabel,
  FormInput,
  FormButton,
  Text,
} from "./ReusmeElements";

class Reusme extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      name: "",
      msg: "false",
    };
  }
  //handle input
  handleName = (e) => {
    this.setState({ name: e.target.value });
  };
  handleEmail = (e) => {
    this.setState({ email: e.target.value });
  };

  //end of handle inputs
  render() {
    return (
      <Containner>
        <FormWrap>
          <Icon to="/">Googi</Icon>
          <FormContent>
            <Form
              id="contact-form"
              // action="/nodemailerTest"
              method="POST"
            >
              <FormH1>
                E-Mail와 간단한 정보 입력해주시면 보내드리도록 하겠습니다.
                감사합니다.
              </FormH1>
              <FormLabel htmlFor="for">Email</FormLabel>
              <FormInput
                type="email"
                name="email"
                placeholder="이메일을 입력해주세요."
                value={this.state.email}
                onChange={() => this.handleEmail}
                required
              />
              <FormLabel htmlFor="for">성함 및 소속</FormLabel>
              <FormInput
                type="name"
                name="name"
                placeholder="성함이나 소속을 입력해주세요."
                value={this.state.name}
                onChange={() => this.handleName}
                required
              />
              <FormButton type="submit">제출</FormButton>
              <Text className="msg">제출 되었습니다.</Text>
            </Form>
          </FormContent>
          <Text>Copyright © 2020 Googi All Rights Reserved.</Text>
        </FormWrap>
      </Containner>
    );
  }
}
export default Reusme;
