import styled from "styled-components";
import { MdKeyboardArrowRight, MdArrowForward } from "react-icons/md";

export const ActionContainer = styled.div`
  background: #0c0c0c;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 30px;
  height: 800px;
  position: relative;
  z-index: 1;
  :before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        180deg,
        rgba(0, 0, 0, 0.2) 0%,
        rgba(0, 0, 0, 0.6) 100%
      ),
      linear-gradient(180deg, rgba(0, 0, 0, 0.2) 0%, transparent 100%);
    z-index: 2;
  }
`;
// 이전
export const ActionBg = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
`;
export const VideoBg = styled.video`
  width: 100%;
  height: 100%;
  --o-object-fit: cover;
  object-fit: cover;
  background: #2c2c2c;
  opacity: 0.3;
`;
export const ActionContent = styled.div`
  z-index: 3;
  max-width: 1200px;
  position: absolute;
`;
export const ActionH1 = styled.h1`
  font-weight: 100;
  color: #fff;
  font-size: 50px;
  text-align: justify;
  @media screen and (max-width: 768px) {
    font-size: 40px;
  }
  @media screen and (max-width: 480px) {
    font-size: 32px;
  }
`;
export const ActionH2 = styled.h2`
  font-weight: 100;
  color: #fff;
  font-size: 30px;
  bottom: 400px;
  transition-delay: 10s;
`;
export const ActionP = styled.div`
  margin-top: 24px;
  color: #fff;
  font-size: 24px;
  max-width: 600px;
  @media screen and (max-width: 768px) {
    font-size: 40px;
  }
  @media screen and (max-width: 480px) {
    font-size: 32px;
  }
`;
export const ActionBtnWrapper = styled.div`
  margin-top: 32px;
  display: flex;
  flex-direction: column;
  align-items: center;
  color: #000000;
`;
export const ArrowForward = styled(MdArrowForward)`
  bottom: 10;
  margin-left: 10px;
  font-size: 16px;
`;
export const ArrowRight = styled(MdKeyboardArrowRight)`
  margin-left: 10px;
  font-size: 16px;
`;
